import React, {useState} from 'react';

const ExpenseForm = () => {
    const [title , setTitle]  = useState('');

    const onTitleChange = () => {
        setTitle(event['title']['value']);
        console.log(title);
    }

    return <form>
        <div className="">
            <label> Title</label>
            <input type="text" onChange={onTitleChange } ></input>
        </div>
        <div className="">
            <label> Amount</label>
            <input type="number" onChange></input>
        </div>

        <div className="">
            <label>Date</label>
            <input type="date" onChange min="2018-01-01" max="2022-12-31"></input>
        </div>
        <button type="submit">Add EXpense</button>
    </form>
};
export default ExpenseForm;
