import React, {useState} from 'react';

const ExpenseForm = () => {
    const [title , setTitle]  = useState('');
    const [amount , setAmount]  = useState('');
    const [date , setDate]  = useState('');

    const onTitleChange = (event) => {
        setTitle(event['target']['value']);console.log(title);
    }
    const onAmountChange = (event) => {
        setAmount(event['target']['value']);
        console.log(amount);
    }
    const onDateChange = (event) => {
        setDate(event['target']['value']);
        console.log(date);
    }

    const onSubmitExpense = (event) =>{
        event.preventDefault();
        const submittedData = {
            title: title,
            amount: amount,
            date: date
        };
        console.table( submittedData);
    }

    return <form onSubmit={onSubmitExpense} >
        <div className="">
            <label> Title</label>
            <input type="text" onChange={onTitleChange } ></input>
        </div>
        <div className="">
            <label> Amount</label>
            <input type="number" onChange={onAmountChange }></input>
        </div>

        <div className="">
            <label>Date</label>
            <input type="date" onChange={onDateChange } min="2018-01-01" max="2022-12-31"></input>
        </div>
        <button type="submit" >Add EXpense</button>
    </form>
};
export default ExpenseForm;
