import React from 'react';
import ExpenseForm from './ExpenseForm';
import Select from 'react-select'

const NewExpense = (props) => {

    const SubmitForm = (submittedData) => {
        const expenseData = {
            ...submittedData,
            id: Math.random().toString()
        }
        console.table(expenseData);
    }

    const onYearFilter = (event) => {
        console.log(event)
    }

    const options = [
        { value: '2018', label: '2018' },
        { value: '2019', label: '2019' },
        { value: '2020', label: '2020' }
    ]

    return <div>
        <div style={{ width: "200px", marginLeft:"auto", marginRight:"auto", marginTop:"30px" }} >
            <Select options={options} onChange={onYearFilter} />
        </div>
        <ExpenseForm onSubmitForm={SubmitForm} />
    </div>
};

export default NewExpense;
